import React, { createContext, useEffect, useState } from "react";
export const ShopContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    for (let index = 0; index < 300+1; index++) {
        cart[index] = 0;
    }
    return cart;
};

  const ShopContextProvider = (props) => {
    const [all_product, setAll_Product] = useState([]);

    const [cartItems, setCartItems] = useState(getDefaultCart());

    useEffect(()=>{
        fetch('http://localhost:4001/allproducts')
        .then((response)=>response.json())
        .then((data)=> setAll_Product(data))
    },[])

    // console.log("cart item", cartItems);
    const addToCart = (itemsId) => {
        setCartItems(prev => ({
          ...prev,
          [itemsId]: (prev[itemsId] || 0) + 1
        }));

        if (localStorage.getItem('auth-token')) {
          fetch('http://localhost:4001/addtocart', {
            method: 'POST',
            headers: {
              Accept: 'application/form-data',
              'auth-token': `${localStorage.getItem('auth-token')}`,
              'Content-Type': 'application/json',
            },
            body:JSON.stringify({"itemsId":itemsId}),
          })
          .then((response) => response.json())
          .then((data) => console.log(data))
          .catch((error) => console.error('Error:', error));
        }
      };


    const removeFromCart = (itemsId) => {
        setCartItems(prev => ({
            ...prev,
            [itemsId]: prev[itemsId] - 1
        }));
    };

    const getTotalCartAmount =() =>{
        let totalAmount = 0;
        for (const items in cartItems ){
            if(cartItems[items] > 0){
            let itemInfo = all_product.find((product) => product.id === Number(items));

            totalAmount += cartItems[items] * itemInfo.new_price;
        }
    }
    return totalAmount;

}

    const getTotalCartItems = ()=>{
        let totalItem = 0;
        for(const item in cartItems){
            if(cartItems[item] > 0){
                totalItem+= cartItems[item]; 
            }
        }
        return totalItem;
    }

    const ContextValue = {getTotalCartItems,getTotalCartAmount,all_product, cartItems, addToCart, removeFromCart };

    return (
        <ShopContext.Provider value={ContextValue}>
            {props.children}
        </ShopContext.Provider>
    );
};

export default ShopContextProvider;
