import React, { useContext } from 'react';
import './CartItem.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';

const CartItem = () => {
  const { all_product, cartItems, removeFromCart } = useContext(ShopContext);

  return (
    <div className='cartitems'>
      <div className="cartitems-main-format">
        <p>Product</p>
        <p>Title</p>
        <p>Quantity</p>
        <p>Price</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {Object.keys(cartItems).map(itemId => {
        const quantity = cartItems[itemId];
        const product = all_product.find(product => product.id === itemId);

        if (quantity > 0 && product) {
          return (
            <div key={itemId}>
              <div className="cartitems-format">
                <img src={product.image} alt="" className='carticons-product-icon' />
                <p>{product.name}</p>
                <p>${product.new_price}</p>
                <button className='cartitems-quantity'>{quantity}</button>
                <p>${product.new_price * quantity}</p>
                <img src={remove_icon} onClick={() => { removeFromCart(itemId) }} alt="" />
              </div>
              <hr />
            </div>
          );
        } else {
          return null; // Skip rendering if quantity is 0 or product not found
        }
      })}
    </div>
  );
}

export default CartItem;
