import React, { useContext } from 'react';
import './CartItem.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';

const CartItem = () => {
    const { all_products, cartItem, removeFromCart } = useContext(ShopContext);

    return (
        <div className='cartitems'>
            <div className="cartitems main-format">
                <p>Product</p>
                <p>Title</p>
                <p>Quantity</p>
                <p>Price</p>
                <p>Total</p>
                <p>Remove</p>
            </div>
            <hr />
            {all_products.map(product => {
                if (cartItem[product.id] > 0) {
                    return (
                        <div key={product.id}>
                            <div className="cartitems-format">
                                <img src={product.image} alt="" className='carticons-product-icon' />
                                <p>{product.name}</p>
                                <p>${product.new_price}</p>
                                <button className='cartitems-quantity'>{cartItem[product.id]}</button>
                                <p>{product.new_price * cartItem[product.id]}</p>
                                <img src={remove_icon} onClick={() => { removeFromCart(product.id) }} alt="" />
                            </div>
                            <hr />
                        </div>
                    );
                }
                return null;
            })}
        </div>
    );
}

export default CartItem;
