import React, { useState } from 'react';
import './ProductDisplay.css';
import star_icon from '../Assets/star_icon.png';
import dull_icon from '../Assets/star_dull_icon.png';

const ProductDisplay = ({ product }) => {
    const [showAdditionalImages, setShowAdditionalImages] = useState(false);

    const handleImageClick = () => {
        // Toggle the state to show/hide additional images
        setShowAdditionalImages(!showAdditionalImages);
    };

    return (
        <div className='productdisplay'>
            <div className="productdisplay-left">
                <div className="productdisplay-img-list">
                    {showAdditionalImages && (
                        <>
                            <img src={product.image} alt="" />
                            <img src={product.image} alt="" />
                            <img src={product.image} alt="" />
                            <img src={product.image} alt="" />
                        </>
                    )}
                </div>
                <div className="productdisplay-img">
                    <img className='productdisplay-main-img' src={product.image} alt="" onClick={handleImageClick} />
                </div>
            </div>
            <div className="productdisplay-right">
                <h1>{product.name}</h1>
                <div className="productdisplay-right-start">
                    <img src={star_icon} alt="" />
                    <img src={star_icon} alt="" />
                    <img src={star_icon} alt="" />
                    <img src={star_icon} alt="" />
                    <img src={dull_icon} alt="" />
                    <p>(122)</p>
                </div>
            </div>
        </div>
    );
};

export default ProductDisplay;
